riurik.wrapErrorHandler = function(handler, func) {
	var l = handler;
	if ( typeof handler == 'function' ) {
		return function() {
			l.apply(l, arguments);
			func.apply(func, arguments);	
		};
	}else{
		return function() {
			func.apply(func, arguments);	
		};
	}
};

